import { Avatar } from '@mui/material'
import React from 'react'
import { SxProps } from '@mui/material'

interface Props {
  name: string
  imageUrl: string | null
  sx: SxProps
}

const ImageOrInitial = ({ name, imageUrl }: Props): JSX.Element => {

  if (imageUrl) return <Avatar alt="user_avatar" src={imageUrl} />
  return (
    <Avatar
      sx={{ bgcolor: '#5bbff1' }}
      alt="user_initials"
    >
      {name[0].toUpperCase()}
    </Avatar>
  )
}

export default ImageOrInitial
