import React, { useState, MouseEvent } from 'react'
import { Logout } from '@mui/icons-material'
import { gql, useMutation, useQuery } from '@apollo/client'
import {
  Box,
  Avatar,
  Menu,
  MenuItem,
  ListItemIcon,
  Divider,
  IconButton,
  Tooltip
} from '@mui/material'
import Loading from '../shared/Loading';

const CURRENT_USER = gql`
query CurrentUser {
  currentUser{
    firstName
    imageUrl
  }
}
`;

const LOGOUT_USER = gql`
mutation LogoutUser(
  $email: String!
) {
  logoutUser(
    input: {
      email: $email,
    }
  ){
    id
  }
}
`;

export default function AccountMenu(): JSX.Element {
  const [logout] = useMutation(LOGOUT_USER, {
    onCompleted: () => {
      window.location.replace('/log_in')
    }
  })
  const { data, loading } = useQuery(CURRENT_USER)

  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null)
  const open = Boolean(anchorEl)

  const handleClick = (event: MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget)
  }
  const handleClose = (): void => {
    setAnchorEl(null)
  }

  const redirectToProfile = (): void => window.location.replace('/profile')

  if (loading) return <Loading />
  return (
    <>
      <Box sx={{ display: 'flex', alignItems: 'center', textAlign: 'center' }}>
        <Tooltip title="Account settings">
          <IconButton
            onClick={handleClick}
            size="medium"
            sx={{ paddingTop: 0 }}
            aria-controls={open ? 'account-menu' : undefined}
            aria-haspopup="true"
            aria-expanded={open ? 'true' : undefined}
          >
            {/* {<Avatar alt='user_avatar' src={require(data.currentUser.imageUrl)} /> */}
            <Avatar sx={{ width: 40, height: 40 }}>{data?.currentUser.firstName[0].toUpperCase()}</Avatar>
          </IconButton>
        </Tooltip>
      </Box>
      <Menu
        anchorEl={anchorEl}
        id="account-menu"
        open={open}
        onClose={handleClose}
        onClick={handleClose}
        PaperProps={{
          elevation: 0,
          sx: {
            overflow: 'visible',
            filter: 'drop-shadow(0px 2px 8px rgba(0,0,0,0.32))',
            mt: 1.5,
            '& .MuiAvatar-root': {
              width: 32,
              height: 32,
              ml: -0.5,
              mr: 1,
            },
            '&::before': {
              content: '""',
              display: 'block',
              position: 'absolute',
              top: 0,
              right: 14,
              width: 10,
              height: 10,
              bgcolor: 'background.paper',
              transform: 'translateY(-50%) rotate(45deg)',
              zIndex: 0,
            },
          },
        }}
        transformOrigin={{ horizontal: 'right', vertical: 'top' }}
        anchorOrigin={{ horizontal: 'right', vertical: 'bottom' }}
      >
        <MenuItem onClick={redirectToProfile}>
          <Avatar /> Profile
        </MenuItem>
        <Divider />
        <MenuItem onClick={() => logout()}>
          <ListItemIcon>
            <Logout fontSize="small" />
          </ListItemIcon>
          Logout
        </MenuItem>
      </Menu>
    </>
  )
}
