import React, { SyntheticEvent, useState } from 'react'
import { styled } from '@mui/material/styles'
import { Tabs, Tab, Box, Grid, Paper } from '@mui/material'
import EditProfile from './EditProfile'

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}))

export default function ProfileWrapper(): JSX.Element {
  const [value, setValue] = useState(0)

  const handleChange = (event: SyntheticEvent, newValue: number) => setValue(newValue)

  return (
    <Box sx={{ width: '100%' }}>
      <Box>
        <Tabs value={value} onChange={handleChange}>
          <Tab label='Introductory Video' />
          <Tab label='Answers' />
        </Tabs>
      </Box>
      <TabPanel value={value} index={0}>
        <CardMedia
          component='video'
          image={data?.video?.url}
          title='Introductory video'
          controls
          autoPlay
        />
      </TabPanel>
      <TabPanel value={value} index={1}>
        <Grid container component={'div'}>
          {data?.user?.answers.map((answer, index) => {
            return (
              <Grid item lg={6} xs={12} key={index}>
                <CardMedia
                  component='video'
                  image={answer.url}
                  title='answer'
                  controls
                  sx={{ padding: '.5rem' }}
                />
              </Grid>
            )
          })}
        </Grid>
      </TabPanel>
    </Box>
  )
}
