import React, { useEffect, useState } from 'react'
import { gql, useMutation } from '@apollo/client'
import { Form, Formik } from 'formik'
import EyeOutlined from '@ant-design/icons/EyeOutlined'
import EyeInvisibleOutlined from '@ant-design/icons/EyeInvisibleOutlined'
import {
  Button,
  FormControl,
  FormHelperText,
  Grid,
  InputAdornment,
  IconButton,
  InputLabel,
  OutlinedInput,
  Stack,
  Typography,
  Box,
} from '@mui/material'
import { strengthColor, strengthIndicator } from '../utils/passwordStrength'
import { SignUpSchema } from '../schema'
import { Password } from '@mui/icons-material'

const initialValues = {
  firstName: '',
  lastName: '',
  email: '',
  password: '',
  phone: '',
  avatar: '',
}

const REGISTER_USER = gql`
  mutation RegisterUser(
    $email: String!,
    $password: String!,
    $phone: String!,
    $firstName: String!,
    $lastName: String!,
    $image: Upload!,
  ) {
    registerUser(
      input: {
        email: $email,
        password: $password,
        phone: $phone,
        lastName: $lastName,
        firstName: $firstName,
        image: $image
      }
    ){
      lastName
    }
  }
`;
export default function RegisterForm(): JSX.Element {
  const [register, { loading, error }] = useMutation(REGISTER_USER)
  const [level, setLevel] = useState({ color: '', label: '' })
  const [showPassword, setShowPassword] = useState(false)

  const handleClickShowPassword = () => {
    setShowPassword(!showPassword)
  }

  const handleMouseDownPassword = (event) => {
    event.preventDefault()
  }

  const changePasswordConfirmation = (value) => {
    console.log(value)
  }
  const changePassword = (value: string): void => {
    const temp = strengthIndicator(value)
    setLevel(strengthColor(temp))
  }

  const handleSubmit = (event) => {
    const formData = new FormData()
    register({
      variables: {
        firstName: event.firstName,
        lastName: event.lastName,
        email: event.email,
        phone: event.phone,
        image: event.image,
        password: event.password
      }
    })
  }

  useEffect(() => {
    changePassword('')
  }, [])

  return (
    <>
      <Formik
        onSubmit={handleSubmit}
        initialValues={initialValues}
        validationSchema={SignUpSchema}
      >
        {({ errors, handleBlur, handleChange, handleSubmit, isSubmitting, touched, values }) => (
          <form noValidate onSubmit={handleSubmit} encType="multipart/form-data">
            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                <Stack spacing={1}>
                  <InputLabel htmlFor='firstname'>First Name*</InputLabel>
                  <OutlinedInput
                    id='firstName'
                    type='firstName'
                    value={values.firstName}
                    name='firstName'
                    onBlur={handleBlur}
                    onChange={handleChange}
                    fullWidth
                    error={Boolean(touched.firstName && errors.firstName)}
                    label='First Name'
                  />
                </Stack>
                {touched.firstName && typeof errors.firstName === 'string' && (
                  <FormHelperText error id='helper-text-firstname-signup'>{errors.firstName}</FormHelperText>
                )}
              </Grid>
              <Grid item xs={12} md={6}>
                <Stack spacing={1}>
                  <InputLabel htmlFor='lastName'>Last Name*</InputLabel>
                  <OutlinedInput
                    fullWidth
                    error={Boolean(touched.lastName && errors.lastName)}
                    id='lastName'
                    type='lastName'
                    value={values.lastName}
                    name='lastName'
                    onBlur={handleBlur}
                    onChange={handleChange}
                    label='lastName'
                  />
                </Stack>
                {touched.lastName && typeof errors.lastName === 'string' && (
                  <FormHelperText error id='helper-text-lastname-signup'>
                    {errors.lastName}
                  </FormHelperText>
                )}
              </Grid>
              <Grid item xs={12} md={6}>
                <Stack spacing={1}>
                  <InputLabel htmlFor='email'>Email Address*</InputLabel>
                  <OutlinedInput
                    fullWidth
                    error={Boolean(touched.email && errors.email)}
                    id='email'
                    type='email'
                    value={values.email}
                    name='email'
                    onBlur={handleBlur}
                    onChange={handleChange}
                    label='Email'
                  />
                </Stack>
                {touched.email && typeof errors.email === 'string' && (
                  <FormHelperText error id='helper-text-email-signup'>
                    {errors.email}
                  </FormHelperText>
                )}
              </Grid>
              <Grid item xs={12} md={6}>
                <Stack spacing={1}>
                  <InputLabel htmlFor='email'>Phone</InputLabel>
                  <OutlinedInput
                    fullWidth
                    error={Boolean(touched.phone && errors.phone)}
                    id='phone'
                    type='phone'
                    value={values.phone}
                    name='phone'
                    onBlur={handleBlur}
                    onChange={handleChange}
                    label='Phone Number'
                  />
                </Stack>
                {touched.phone && typeof errors.phone === 'string' && (
                  <FormHelperText error id='helper-text-phone-signup'>
                    {errors.phone}
                  </FormHelperText>
                )}
              </Grid>
              <Grid item xs={12}>
                <Stack spacing={1}>
                  <InputLabel htmlFor='email'>Image*</InputLabel>
                  <OutlinedInput
                    fullWidth
                    error={Boolean(touched.image && errors.image)}
                    id='image'
                    type='file'
                    value={values.image}
                    name='image'
                    onBlur={handleBlur}
                    onChange={handleChange}
                  />
                </Stack>
                {touched.image && typeof errors.image === 'string' && (
                  <FormHelperText error id='helper-text-phone-signup'>
                    {errors.image}
                  </FormHelperText>
                )}
              </Grid>
              <Grid item xs={12}>
                <Stack spacing={1}>
                  <InputLabel htmlFor='password'>Password*</InputLabel>
                  <OutlinedInput
                    fullWidth
                    error={Boolean(touched.password && errors.password)}
                    id='password'
                    type={showPassword ? 'text' : 'password'}
                    value={values.password}
                    name='password'
                    onBlur={handleBlur}
                    onChange={(e) => {
                      handleChange(e)
                      changePassword(e.target.value)
                    }}
                    endAdornment={
                      <InputAdornment position='end'>
                        <IconButton
                          aria-label='toggle password visibility'
                          onClick={handleClickShowPassword}
                          onMouseDown={handleMouseDownPassword}
                          edge='end'
                          color='secondary'
                        >
                          {showPassword ? <EyeOutlined /> : <EyeInvisibleOutlined />}
                        </IconButton>
                      </InputAdornment>
                    }
                    placeholder='Enter password'
                  />
                </Stack>
                {values.password && (
                  <FormControl fullWidth sx={{ mt: 2 }}>
                    <Grid container spacing={2} alignItems='center'>
                      <Grid item>
                        <Box sx={{ bgcolor: level?.color, width: 200, height: 8, borderRadius: '7px' }} />
                      </Grid>
                      <Grid item>
                        <Typography variant='subtitle1' fontSize='0.75rem'>
                          {level?.label}
                        </Typography>
                      </Grid>
                    </Grid>
                  </FormControl>
                )}
              </Grid>
              <Grid item xs={12}>
                <Stack spacing={1}>
                  <InputLabel htmlFor='password-confirmation'>Comfirm Password</InputLabel>
                  <OutlinedInput
                    fullWidth
                    error={Boolean(touched.passwordConfirmation && errors.passwordConfirmation)}
                    id='passwordConfirmation'
                    type={showPassword ? 'text' : 'password'}
                    value={values.passwordConfirmation}
                    name='passwordConfirmation'
                    onBlur={handleBlur}
                    // onChange={(e) => {
                    //   handleChange(e)
                    //   changePasswordConfirmation(e.target.value)
                    // }}
                    endAdornment={
                      <InputAdornment position='end'>
                        <IconButton
                          aria-label='toggle password visibility'
                          onClick={handleClickShowPassword}
                          onMouseDown={handleMouseDownPassword}
                          edge='end'
                          color='secondary'
                        >
                          {showPassword ? <EyeOutlined /> : <EyeInvisibleOutlined />}
                        </IconButton>
                      </InputAdornment>
                    }
                    placeholder='Enter password'
                  />
                </Stack>
                {touched.passwordConfirmation && typeof errors.passwordConfirmation === 'string' && (
                  <FormHelperText error id='helper-text-password-signup'>
                    {errors.passwordConfirmation}
                  </FormHelperText>
                )}
              </Grid>
              <Grid item xs={12}>
                <Button disableElevation disabled={isSubmitting} fullWidth size='large' type='submit' variant='contained' color='primary'>
                  Create Account
                </Button>
              </Grid>
            </Grid>
          </form>
        )}
      </Formik>
    </>
  )
}
