import React, { useState } from 'react'
import { Formik } from 'formik'
import {
  Button,
  FormHelperText,
  Grid,
  Stack,
  TextField,
} from '@mui/material'
import { forgotPasswordSchema } from '../schema'
import Banner from '../shared/Banner'
import { gql, useMutation } from '@apollo/client'

const initialValue = {
  email: '',
}

const FORGOT_PASSWORD = gql`
mutation ForgotPassword(
  $email: String!,
) {
  forgotPassword(
    input: {
      email: $email,
    }
  ){
    email
  }
}
`;

export default function ForgotPasswordForm(): JSX.Element {

  const [message, setMessage] = useState('')
  const [severity, setSeverity] = useState(null)

  const [forgotPassword, { loading }] = useMutation(FORGOT_PASSWORD, {
    onCompleted: (data) => {
      if (!!data) {
        setSeverity('success')
        setMessage('Account Change request has been sent to your email')
      }
    },
    onError: () => {
      setSeverity('error')
      setMessage('Sorry, account change request was unsuccessful, please try again')
    }
  })

  const handleSubmit = (event) => {
    mutate({
      variables: {
        firstName: event.firstName,
        lastName: event.lastName,
        email: event.email,
        phone: event.phone,
        password: event.password,
      }
    })
  }

  return (
    <Formik
      onSubmit={handleSubmit}
      initialValues={initialValue}
      validationSchema={forgotPasswordSchema}
    >
      {({ errors, handleBlur, handleChange, handleSubmit, isSubmitting, touched, values }) => (
        <>
          {message && <Banner severity={severity} message={message} />}
          <form noValidate onSubmit={handleSubmit}>
            <Grid container spacing={3}>
              <Grid item xs={12}>
                <Stack spacing={1}>
                  <TextField
                    id='email'
                    type='email'
                    value={values.email}
                    name='email'
                    placeholder='Email'
                    onChange={handleChange}
                    onBlur={handleBlur}
                    fullWidth
                    error={Boolean(touched.email && errors.email)}
                  />
                </Stack>
                {touched.email && typeof errors.email === 'string' && (
                  <FormHelperText error id='standard-weight-helper-text-email-forgot-Password'>
                    {errors.email}
                  </FormHelperText>
                )}
              </Grid>
              <Grid item xs={12}>
                <Button
                  disableElevation
                  disabled={isSubmitting}
                  fullWidth
                  size='large'
                  type='submit'
                  variant='contained'
                  sx={{
                    background: '#5bbff1',
                    color: '#FFF'
                  }}
                >
                  Request Password change
                </Button>
              </Grid>
            </Grid>
          </form>
        </>
      )}
    </Formik>
  )
}
