import React, { useEffect, useState } from 'react'
import { gql, useMutation } from '@apollo/client'
import { Formik, useFormikContext } from 'formik'
import {
  Button,
  FormHelperText,
  Grid,
  InputLabel,
  OutlinedInput,
  Stack,
} from '@mui/material'
import { SignUpSchema } from '../schema'
import Banner from '../shared/Banner'
import { User } from '../interface/index'

const UPDATE_USER = gql`
  mutation UpdateUser(
    $email: String,
    $password: String,
    $phone: String,
    $firstName: String,
    $lastName: String,
    $image: String
  ) {
    updateUser(
      input: {
        email: $email,
        password: $password,
        phone: $phone,
        lastName: $lastName,
        firstName: $firstName,
        image: $image
      }
    ){
      email,
      password,
      phone,
      lastName,
      firstName,
      imageUrl
    }
  }
`;

const initial = {
  firstName: '',
  lastName: '',
  email: '',
  password: '',
  phone: '',
  passwordConfirmation: ''
}

export default function EditUserForm({ initialValues }): JSX.Element {
  const [message, setMessage] = useState('')
  const [severity, setSeverity] = useState(null)
  const { setValues } = useFormikContext<User>()

  const [mutate, { loading }] = useMutation(UPDATE_USER, {
    onCompleted: (data) => {
      if (!!data) {
        setSeverity('success')
        setMessage('Account was created successfully, a confirmation link was sent to your email')
      }
    },
    onError: () => {
      setSeverity('error')
      setMessage('Sorry, account creation was unsuccessful')
    }
  })

  useEffect(() => {
    // setValues(initialValues)
  }, [])

  const handleSubmit = (event: User): void => {
    console.log("the more the whole world in his hands", event)
    mutate({
      variables: {
        firstName: event.firstName,
        lastName: event.lastName,
        image: event.imageUrl,
        email: event.email,
        phone: event.phone,
        password: event.password,
      }
    })
  }

  return (

    // {({ errors, handleBlur, handleChange, handleSubmit, touched, values }) => (
    <>
      {message && <Banner severity={severity} message={message} />}
      <form noValidate onSubmit={handleSubmit}>
        <Grid container spacing={3}>
          <Grid item xs={12} md={6}>
            <Stack spacing={1}>
              <InputLabel htmlFor='firstname'>First Name*</InputLabel>
              <OutlinedInput
                id='firstName'
                type='firstName'
                value={values.firstName}
                name='firstName'
                onBlur={handleBlur}
                fullWidth
                error={Boolean(touched.firstName && errors.firstName)}
                placeholder='First Name'
              />
            </Stack>
            {touched.firstName && typeof errors.firstName === 'string' && (
              <FormHelperText error id='helper-text-firstname-signup'>{errors.firstName}</FormHelperText>
            )}
          </Grid>
          <Grid item xs={12} md={6}>
            <Stack spacing={1}>
              <InputLabel htmlFor='lastName'>Last Name*</InputLabel>
              <OutlinedInput
                fullWidth
                error={Boolean(touched.lastName && errors.lastName)}
                id='lastName'
                type='lastName'
                value={values.lastName}
                name='lastName'
                onBlur={handleBlur}
                onChange={handleChange}
                placeholder='Last Name'
              />
            </Stack>
            {touched.lastName && typeof errors.lastName === 'string' && (
              <FormHelperText error id='helper-text-lastname-signup'>
                {errors.lastName}
              </FormHelperText>
            )}
          </Grid>
          <Grid item xs={12} md={6}>
            <Stack spacing={1}>
              <InputLabel htmlFor='email'>Email Address*</InputLabel>
              <OutlinedInput
                fullWidth
                error={Boolean(touched.email && errors.email)}
                id='email'
                type='email'
                value={values.email}
                name='email'
                onBlur={handleBlur}
                onChange={handleChange}
                placeholder='Email'
              />
            </Stack>
            {touched.email && typeof errors.email === 'string' && (
              <FormHelperText error id='helper-text-email-signup'>
                {errors.email}
              </FormHelperText>
            )}
          </Grid>
          <Grid item xs={12} md={6}>
            <Stack spacing={1}>
              <InputLabel htmlFor='email'>Phone</InputLabel>
              <OutlinedInput
                fullWidth
                error={Boolean(touched.phone && errors.phone)}
                id='phone'
                type='phone'
                value={values.phone}
                name='phone'
                onBlur={handleBlur}
                onChange={handleChange}
                placeholder='Phone Number'
              />
            </Stack>
            {touched.phone && typeof errors.phone === 'string' && (
              <FormHelperText error id='helper-text-phone-signup'>
                {errors.phone}
              </FormHelperText>
            )}
          </Grid>

          <Grid item xs={12}>
            <Button
              disableElevation
              fullWidth
              disabled={loading}
              size='large'
              type='submit'
              variant='contained'
              sx={{
                background: '#5bbff1',
                color: '#FFF'
              }}
            // onClick={() => handleSubmit()}
            >
              Update Account
            </Button>
          </Grid>
        </Grid>
      </form>
    </>

  )
}
